['#include <stdio.h>
int min(int x,int y)
{
    if(x>y)
    {
        return y;
    }
    else
    {
        return x;
    }
    
}

int distance_clock(char a,char b)
{
    if(b>a)
    {
        return b-a;
    }
    else if(a==b)
        return 0;
    else
    {
        return ('z'-a)+(b-'a')+1;
    }
    
}

int distance_anti(char a,char b)
{
    if(a>b)
    {
        return a-b;
    }
    else if(a==b)
        return 0;
    else
    {
        return (a-'a')+('z'-b)+1;
    }
    
}


