{
    char S[150];
    scanf("%s",S);
    char pointer='a';
    int i=0;
    int count=0;
    while(S[i]!='\0')
    {
        count+=min(distance_clock(pointer,S[i]),distance_anti(pointer,S[i]));
        printf("%d ",count);
        pointer=S[i];
        i++;
    }
    printf("%d",count);
    return 0;
}']

