int abs(int a)
{
    if(a<0) return -a;
    else return a;
}

int min(int a, int b)
{
    if(a<b) return a;
    else return b;
}

int main()
{
    int i, x, m, n, c=0;
    char s[106];

    scanf("%s", s);

    int a=26;

    for(i=0 ; i<strlen(s) ; i++)
    {
        x=s[i]-97;
        m=abs(a-x);
        n=26-m;
        c+=min(m, n);
        a=x;
    }
    printf("%d", c);

    return 0;
}
