['#include<stdio.h>
#include<string.h>
int main(void)
{
    char s[110];
    int sum=0;
    int i;
    static char pre='a';
    scanf("%s",s);
    for(i=0;s[i];pre=s[i],i++)
    {

        if((pre-s[i]+26)%26<=13)
        {
            sum+=(pre-s[i]+26)%26;
        }
        else
        {
            sum+=26-(pre-s[i]+26)%26;
        }
    }
    printf("%d",sum);
    return 0;
}
